# Longest Duplicate Substring
# Given a string S, consider all duplicated substrings: (contiguous) substrings of S
# that occur 2 or more times.  (The occurrences may overlap.)
#
# Return any duplicated substring that has the longest possible length.  (If S does not
# have a duplicated substring, the answer is "".)
#
# Example 1:
# Input: "banana"
# Output: "ana"
#
# Example 2:
# Input: "abcd"
# Output: ""
#
# Note:
# - 2 <= S.length <= 10^5
# - S consists of lowercase English letters.

import testlib
from typing import Callable, Optional
from functools import reduce


class Solution:
    def longestDupSubstring(self, S: str) -> str:
        # this is based on https://massivealgorithms.blogspot.com/2019/06/leetcode-1044-longest-duplicate.html
        # but adds checking to detect false positives and uses a prime modulus
        char_nums = [ord(c) - ord("a") for c in S]
        modulus = 2 ** 31 - 1  # use a prime to avoid collisions i think?

        def find_matching_index(length: int) -> int:
            first_char_factor = pow(26, length, modulus)
            current_hash = reduce(
                lambda acc, c: (acc * 26 + c) % modulus, char_nums[0:length], 0
            )
            seen = {current_hash: 0}
            for i in range(length, len(S)):
                current_hash = (
                    current_hash * 26
                    + char_nums[i]  # add new character at end of hash
                    - char_nums[i - length]
                    * first_char_factor  # and remove first character from start
                ) % modulus
                if current_hash in seen:
                    match_start = seen[current_hash]
                    if (
                        S[match_start : match_start + length]
                        == S[i - length + 1 : i + 1]
                    ):
                        return i - length + 1
                seen[current_hash] = i - length + 1

        ans_idx, min_length, max_length = 0, 0, len(S)
        while min_length < max_length:
            test_length = (min_length + max_length + 1) // 2
            pos = find_matching_index(test_length)
            if pos:
                min_length = test_length
                ans_idx = pos
            else:
                max_length = test_length - 1
        return S[ans_idx : ans_idx + min_length]

    def my_attempt(self, S: str) -> str:
        return self.binary_search(S, self.check_rabin_karp)

    def check_naive(self, S: str, length: int) -> Optional[str]:
        for start in range(0, len(S) - length):
            word = S[start : start + length]
            if S.find(word, start + 1) > -1:
                return word

    def check_rabin_karp(self, S: str, length: int) -> Optional[str]:
        # this is my attempt and it still has a lot of issues
        print("rabin-karp for length", length)
        modulus = 2 ** 31 - 1
        base_char = ord("a") - 1

        def make_hash(start: int) -> int:
            out = 0
            for i in range(length):
                out = (
                    out + (ord(S[start + i]) - base_char) * (26 ** (length - i - 1))
                ) % modulus
            return out

        sub_amt = 26 ** (length - 1)

        def next_hash(prev_hash: int, new_start: int) -> int:
            prev_start = new_start - 1
            print("old hash value:", prev_hash)
            print("removing", S[prev_start], "at pos", prev_start)
            out = prev_hash
            out -= (ord(S[prev_start]) - base_char) * sub_amt
            out *= 26
            print("adding", S[new_start + length - 1], "at pos", new_start + length - 1)
            out += ord(S[new_start + length - 1]) - base_char
            out = out % modulus
            print("new hash value:", out)
            return out

        for start in range(0, len(S) - length):
            print()
            print("starting search at offset", start)
            hash_to_match = make_hash(start)
            print("initial hash of", S[start : start + length], "is", hash_to_match)
            test_hash = hash_to_match
            for offset_start in range(start + 1, len(S) + 1 - length):
                test_hash = next_hash(test_hash, offset_start)
                if test_hash == hash_to_match:
                    print("found match")
                    if (
                        S[start : start + length]
                        == S[offset_start : offset_start + length]
                    ):
                        return S[start : start + length]

    def binary_search(self, S: str, alg: Callable[[str, int], Optional[str]]) -> str:
        best = ""
        min_length_to_check = 1
        max_length_to_check = len(S) - 1
        while min_length_to_check <= max_length_to_check:
            length_to_check = (min_length_to_check + max_length_to_check) // 2
            ans = alg(S, length_to_check)
            if ans:
                best = ans
                min_length_to_check = length_to_check + 1
            else:
                max_length_to_check = length_to_check - 1

        return best


if __name__ == "__main__":
    testdata = [
        ("banana", "ana"),
        ("abcd", ""),
        (
            "",
            "ddbcddbabee",
        ),
    ]
    testlib.run(
        lambda t, tc: t.assertEqual(Solution().longestDupSubstring(tc[0]), tc[1], tc),
        testdata,
    )

